class DeepseekOcrPreTrainedModel(PreTrainedModel):
    config: DeepseekOcrConfig
    base_model_prefix = "deepseek_ocr"
    main_input_name = "pixel_values"
    input_modalities = "image"
    _no_split_modules = ["DeepseekOcrVisionAttention"]
