## Patcher
class MaskedGroupNorm(torch.nn.GroupNorm):
    def forward(self, x: torch.Tensor, padding_mask: Optional[torch.Tensor] = None) -> torch.Tensor:
        if padding_mask is None:
            return super().forward(x)

        # x: (B, C, L), padding_mask: (B, 1, L) or (B, L)
        if padding_mask.dim() == 2:
            padding_mask = padding_mask.unsqueeze(1)  # (B, 1, L)

        B, C, L = x.shape
        G = self.num_groups

        if padding_mask.shape != x.shape:
            padding_mask = padding_mask.expand_as(x)

        x_grouped = x.view(B, G, C // G, L)
        padding_mask_grouped = padding_mask.view(B, G, C // G, L)
        padding_masked_x = x_grouped * padding_mask_grouped
        padding_mask_sum = padding_mask_grouped.sum(dim=(2, 3), keepdim=True)  # (B, G, 1, 1)
        mean = padding_masked_x.sum(dim=(2, 3), keepdim=True) / padding_mask_sum  # (B, G, 1, 1)
        var = ((padding_masked_x - mean) ** 2 * padding_mask_grouped).sum(dim=(2, 3), keepdim=True) / padding_mask_sum
        x_norm = (x_grouped - mean) / torch.sqrt(var + self.eps)
        x_norm = x_norm.view(B, C, L)
        if self.affine:
            x_norm = x_norm * self.weight.view(1, -1, 1) + self.bias.view(1, -1, 1)
        return x_norm * padding_mask


class ConvBlock1d(torch.nn.Module):
    def __init__(self, config: PerceptionEncoderVideoTransformerConfig):
        super().__init__()
        self.groupnorm = MaskedGroupNorm(num_groups=1, num_channels=config.hidden_size)
        self.activation = torch.nn.SiLU()
        self.project = torch.nn.Conv1d(
            in_channels=config.hidden_size,
            out_channels=config.hidden_size,
            kernel_size=3,
        )

    def _pad(self, x: torch.Tensor) -> int:
        kernel_size = 3
        stride = 1
        length = x.shape[-1]
        padding_total = kernel_size - stride
        n_frames = (length - kernel_size + padding_total) / stride + 1
        ideal_length = (math.ceil(n_frames) - 1) * stride + (kernel_size - padding_total)
        extra_padding = ideal_length - length

        # Asymmetric padding required for odd strides
        padding_right = padding_total // 2
        padding_left = padding_total - padding_right
        return F.pad(x, (padding_left, padding_right + extra_padding), mode="constant", value=0.0)

    def forward(self, x: torch.Tensor, padding_mask: Optional[torch.Tensor] = None) -> torch.Tensor:
        x = self.groupnorm(x, padding_mask=padding_mask)
        x = self.activation(x)
        if padding_mask is not None:
            x = x * padding_mask.unsqueeze(1)
        return self.project(self._pad(x))


class ResnetBlock1d(torch.nn.Module):
    def __init__(self, config: PerceptionEncoderVideoTransformerConfig):
        super().__init__()
        self.block1 = ConvBlock1d(config)
        self.block2 = ConvBlock1d(config)

    def forward(self, x: torch.Tensor, padding_mask: Optional[torch.Tensor] = None) -> torch.Tensor:
        h = self.block1(x, padding_mask=padding_mask)
        h = self.block2(h, padding_mask=padding_mask)
        return h + x


class PerceptionEncoderVideoMLP(nn.Module):
    def __init__(self, config):
        super().__init__()
        self.config = config
        self.hidden_size = config.hidden_size
        self.intermediate_size = config.intermediate_size
        self.gate_proj = nn.Linear(self.hidden_size, self.intermediate_size, bias=False)
        self.up_proj = nn.Linear(self.hidden_size, self.intermediate_size, bias=False)
        self.down_proj = nn.Linear(self.intermediate_size, self.hidden_size, bias=False)
        self.act_fn = ACT2FN[config.hidden_act]

    def forward(self, x):
        down_proj = self.down_proj(self.act_fn(self.gate_proj(x)) * self.up_proj(x))
        return down_proj


@use_kernel_forward_from_hub("RMSNorm")
class PerceptionEncoderVideoRMSNorm(nn.Module):
    def __init__(self, hidden_size, eps: float = 1e-6) -> None:
        """
        PerceptionEncoderVideoRMSNorm is equivalent to T5LayerNorm
        """
        super().__init__()
        self.weight = nn.Parameter(torch.ones(hidden_size))
        self.variance_epsilon = eps

    def forward(self, hidden_states: torch.Tensor) -> torch.Tensor:
        input_dtype = hidden_states.dtype
        hidden_states = hidden_states.to(torch.float32)
        variance = hidden_states.pow(2).mean(-1, keepdim=True)
        hidden_states = hidden_states * torch.rsqrt(variance + self.variance_epsilon)
        return self.weight * hidden_states.to(input_dtype)

    def extra_repr(self):
        return f"{tuple(self.weight.shape)}, eps={self.variance_epsilon}"


class PerceptionEncoderVideoRotaryEmbedding(nn.Module):
    inv_freq: torch.Tensor  # fix linting for `register_buffer`

    def __init__(self, config: PerceptionEncoderVideoConfig, device=None):
        super().__init__()
        # BC: "rope_type" was originally "type"
        if hasattr(config, "rope_scaling") and isinstance(config.rope_scaling, dict):
            self.rope_type = config.rope_scaling.get("rope_type", config.rope_scaling.get("type"))
        else:
            self.rope_type = "default"
        self.max_seq_len_cached = config.max_position_embeddings
        self.original_max_seq_len = config.max_position_embeddings

        self.config = config
        self.rope_init_fn = ROPE_INIT_FUNCTIONS[self.rope_type]

        inv_freq, self.attention_scaling = self.rope_init_fn(self.config, device)
        self.register_buffer("inv_freq", inv_freq, persistent=False)
        self.original_inv_freq = self.inv_freq

    @torch.no_grad()
    @dynamic_rope_update  # power user: used with advanced RoPE types (e.g. dynamic rope)
    def forward(self, x, position_ids):
        inv_freq_expanded = self.inv_freq[None, :, None].float().expand(position_ids.shape[0], -1, 1).to(x.device)
        position_ids_expanded = position_ids[:, None, :].float()

        device_type = x.device.type if isinstance(x.device.type, str) and x.device.type != "mps" else "cpu"
        with torch.autocast(device_type=device_type, enabled=False):  # Force float32
            freqs = (inv_freq_expanded.float() @ position_ids_expanded.float()).transpose(1, 2)
            emb = torch.cat((freqs, freqs), dim=-1)
            cos = emb.cos() * self.attention_scaling
            sin = emb.sin() * self.attention_scaling

        return cos.to(dtype=x.dtype), sin.to(dtype=x.dtype)


def repeat_kv(hidden_states: torch.Tensor, n_rep: int) -> torch.Tensor:
    """
    This is the equivalent of torch.repeat_interleave(x, dim=1, repeats=n_rep). The hidden states go from (batch,
    num_key_value_heads, seqlen, head_dim) to (batch, num_attention_heads, seqlen, head_dim)
    """
    batch, num_key_value_heads, slen, head_dim = hidden_states.shape
    if n_rep == 1:
        return hidden_states
    hidden_states = hidden_states[:, :, None, :, :].expand(batch, num_key_value_heads, n_rep, slen, head_dim)
    return hidden_states.reshape(batch, num_key_value_heads * n_rep, slen, head_dim)


def eager_attention_forward(
    module: nn.Module,
    query: torch.Tensor,
    key: torch.Tensor,
    value: torch.Tensor,
    attention_mask: Optional[torch.Tensor],
    scaling: float,
    dropout: float = 0.0,
    **kwargs: Unpack[TransformersKwargs],
):
    key_states = repeat_kv(key, module.num_key_value_groups)
    value_states = repeat_kv(value, module.num_key_value_groups)

    attn_weights = torch.matmul(query, key_states.transpose(2, 3)) * scaling
    if attention_mask is not None:
        causal_mask = attention_mask[:, :, :, : key_states.shape[-2]]
        attn_weights = attn_weights + causal_mask

    attn_weights = nn.functional.softmax(attn_weights, dim=-1, dtype=torch.float32).to(query.dtype)
    attn_weights = nn.functional.dropout(attn_weights, p=dropout, training=module.training)
    attn_output = torch.matmul(attn_weights, value_states)
    attn_output = attn_output.transpose(1, 2).contiguous()

    return attn_output, attn_weights


def apply_rotary_pos_emb(q, k, freqs_cis, unsqueeze_dim=1):
    freqs_cis = freqs_cis.unsqueeze(unsqueeze_dim)
    q_ = q.reshape(*q.shape[:-1], -1, 1, 2)
    k_ = k.reshape(*k.shape[:-1], -1, 1, 2)
    return (q_ * freqs_cis).sum(5).flatten(3), (k_ * freqs_cis).sum(5).flatten(3)


class PerceptionEncoderVideoAttention(nn.Module):
    """Multi-headed attention from 'Attention Is All You Need' paper"""

    def __init__(self, config, layer_idx):
        super().__init__()
        self.config = config
        self.layer_idx = layer_idx
        self.head_dim = getattr(config, "head_dim", config.hidden_size // config.num_attention_heads)
        self.num_key_value_groups = config.num_attention_heads // config.num_key_value_heads
        self.scaling = self.head_dim**-0.5
        self.attention_dropout = config.attention_dropout
        self.is_causal = False

        self.q_proj = nn.Linear(
            config.hidden_size, config.num_attention_heads * self.head_dim, bias=config.attention_bias
        )
        self.k_proj = nn.Linear(
            config.hidden_size, config.num_key_value_heads * self.head_dim, bias=config.attention_bias
        )
        self.v_proj = nn.Linear(
            config.hidden_size, config.num_key_value_heads * self.head_dim, bias=config.attention_bias
        )
        self.o_proj = nn.Linear(
            config.num_attention_heads * self.head_dim, config.hidden_size, bias=config.attention_bias
        )
        self.q_norm = PerceptionEncoderVideoRMSNorm(
            self.head_dim, eps=config.rms_norm_eps
        )  # unlike olmo, only on the head dim!
        self.k_norm = PerceptionEncoderVideoRMSNorm(
            self.head_dim, eps=config.rms_norm_eps
        )  # thus post q_norm does not need reshape
        self.sliding_window = config.sliding_window if config.layer_types[layer_idx] == "sliding_attention" else None

    @deprecate_kwarg("past_key_value", new_name="past_key_values", version="4.58")
    def forward(
        self,
        hidden_states: torch.Tensor,
        position_embeddings: tuple[torch.Tensor, torch.Tensor],
        attention_mask,
        **kwargs,
    ) -> tuple[torch.Tensor, Optional[torch.Tensor]]:
        # The only difference from `Qwen3Attention` is the reshape of Q/K/V
        # We reshape # B x T x C -> B x T x C/H x H, and then permute...
        # Qwen3 reshapes # B x T x C -> B x T x H x C/H

        input_shape = hidden_states.shape[:-1]
        nheads = hidden_states.size(-1) // self.head_dim

        query_states = self.q_norm(self._reshape_heads(self.q_proj(hidden_states), nheads))
        key_states = self.k_norm(self._reshape_heads(self.k_proj(hidden_states), nheads))
        value_states = self._reshape_heads(self.v_proj(hidden_states), nheads)

        query_states, key_states = apply_rotary_pos_emb(query_states, key_states, position_embeddings)

        attention_interface = eager_attention_forward
        if self.config._attn_implementation != "eager":
            attention_interface = ALL_ATTENTION_FUNCTIONS[self.config._attn_implementation]

        attn_output, attn_weights = attention_interface(
            self,
            query_states,
            key_states,
            value_states,
            attention_mask,
            dropout=0.0 if not self.training else self.attention_dropout,
            scaling=self.scaling,
            **kwargs,
        )

        attn_output = attn_output.reshape(*input_shape, -1).contiguous()
        attn_output = self.o_proj(attn_output)
        return attn_output, attn_weights

    def _reshape_heads(self, x: torch.Tensor, heads: int) -> torch.Tensor:
        B, T, C = x.shape
        # B x T x C -> B x T x C/H x H
        x = x.reshape(B, T, C // heads, heads)
        # B x T x C/H x H -> B x H x T x C/H
        return x.permute(0, 3, 1, 2)


class PerceptionEncoderVideoDecoderLayer(GradientCheckpointingLayer):
    def __init__(self, config: PerceptionEncoderVideoConfig, layer_idx: int):
        super().__init__()
        self.hidden_size = config.hidden_size

        self.self_attn = PerceptionEncoderVideoAttention(config=config, layer_idx=layer_idx)

        self.mlp = PerceptionEncoderVideoMLP(config)
        self.input_layernorm = PerceptionEncoderVideoRMSNorm(config.hidden_size, eps=config.rms_norm_eps)
        self.post_attention_layernorm = PerceptionEncoderVideoRMSNorm(config.hidden_size, eps=config.rms_norm_eps)
        self.attention_type = config.layer_types[layer_idx]

    @deprecate_kwarg("past_key_value", new_name="past_key_values", version="4.58")
    def forward(
        self,
        hidden_states: torch.Tensor,
        attention_mask: Optional[torch.Tensor] = None,
        position_ids: Optional[torch.LongTensor] = None,
        past_key_values: Optional[Cache] = None,
        use_cache: Optional[bool] = False,
        cache_position: Optional[torch.LongTensor] = None,
        position_embeddings: Optional[tuple[torch.Tensor, torch.Tensor]] = None,  # necessary, but kept here for BC
        **kwargs: Unpack[TransformersKwargs],
    ) -> torch.Tensor:
        residual = hidden_states
        hidden_states = self.input_layernorm(hidden_states)
        # Self Attention
        hidden_states, _ = self.self_attn(
            hidden_states=hidden_states,
            attention_mask=attention_mask,
            position_ids=position_ids,
            past_key_values=past_key_values,
            use_cache=use_cache,
            cache_position=cache_position,
            position_embeddings=position_embeddings,
            **kwargs,
        )
        hidden_states = residual + hidden_states

        # Fully Connected
        residual = hidden_states
        hidden_states = self.post_attention_layernorm(hidden_states)
        hidden_states = self.mlp(hidden_states)
        hidden_states = residual + hidden_states
        return hidden_states


def stack_freqs(cos: torch.Tensor, sin: torch.Tensor):
    dim = cos.size(-1)
    cos = cos.narrow(-1, 0, dim // 2)
    sin = sin.narrow(-1, 0, dim // 2)
    freqs_cis = torch.stack((cos, -sin, sin, cos), dim=-1).view(*cos.size(), 2, 2)
    return freqs_cis


class PerceptionEncoderVideoTransformer(torch.nn.Module):
    def __init__(self, config: PerceptionEncoderVideoTransformerConfig):
        super().__init__()
        self.config = config
        self.rope_embeddings = PerceptionEncoderVideoRotaryEmbedding(config)
        self.layers = torch.nn.ModuleList(
            [PerceptionEncoderVideoDecoderLayer(config, layer_idx=i) for i in range(config.num_hidden_layers)]
        )
        self.norm = PerceptionEncoderVideoRMSNorm(config.hidden_size, config.rms_norm_eps)
        self.output = torch.nn.Linear(config.hidden_size, config.hidden_size, bias=False)
        self.x_embedder = ResnetBlock1d(config)
        self.cls_token = torch.nn.Parameter(torch.randn(1, 1, config.hidden_size))

    def forward(
        self,
        x: torch.Tensor,
        *,
        padding_mask: Optional[torch.Tensor] = None,
    ) -> BaseModelOutputWithPooling:
        # Prepend the cls token
        x = torch.cat([self.cls_token.expand(x.size(0), -1, -1), x], dim=1)
        if padding_mask is not None:
            padding_mask = torch.cat([padding_mask[:, [0]], padding_mask], dim=1)

        x = rearrange(x, "b l c-> b c l")
        h = self.x_embedder(x, padding_mask=padding_mask)
        h = rearrange(h, "b c l -> b l c")
        original_N = h.shape[1]
        N = h.shape[1]

        cos, sin = self.rope_embeddings(h, torch.arange(h.size(1), device=h.device)[None])
        rope_embeddings = stack_freqs(cos, sin)

        if padding_mask is not None:
            padding_mask = padding_mask[:, None, None].bool()

        for layer in self.layers:
            h = layer(
                hidden_states=h,
                attention_mask=padding_mask,
                position_embeddings=rope_embeddings,
            )

        # output layer
        if self.norm is not None:
            h = self.norm(h)

        output = self.output(h)
        N = output.shape[1]
        if original_N != N:
            output = output[:, -original_N:]

        return BaseModelOutputWithPooling(last_hidden_state=output[:, 1:], pooler_output=output[:, 0])


class PerceptionEncoderVideoContrastiveHead(torch.nn.Module):
    def __init__(
        self,
        in_dim: int,
        out_dim: int,
    ) -> None:
        super().__init__()
        self.layer_norm = torch.nn.LayerNorm(normalized_shape=in_dim, eps=1e-6)
        self.proj = torch.nn.Linear(in_dim, out_dim, bias=False)

    def forward(self, x: torch.Tensor) -> torch.Tensor:
        return self.proj(self.layer_norm(x))


class PerceptionEncoderVideoPretrainedModel(PreTrainedModel):
    config: PerceptionEncoderVideoConfig
    base_model_prefix = "perception_encoder_video"
    supports_gradient_checkpointing = True
    _supports_sdpa = True
    _supports_flash_attn = True
    _supports_flex_attn = True
    _supports_attention_backend = True


class PerceptionEncoderVideoModel(PerceptionEncoderVideoPretrainedModel):
    def __init__(self, config: PerceptionEncoderVideoConfig):
        super().__init__(config)
        # Synchronize _attn_implementations
        config.transformer._attn_implementation = config._attn_implementation
        # NOTE: we use `AutoModelForImageClassification` instead of `AutoModelForImageClassification`
        # because `TimmWrapperModel` forces `num_classes=0` (https://github.com/huggingface/transformers/blob/53838edde77cb10f3a360150aa85a457637e9ac3/src/transformers/models/timm_wrapper/modeling_timm_wrapper.py#L163)
        # which drops the final linear projection
        self.clip_vision_model = AutoModelForImageClassification.from_config(config.clip_vision_model)
        self.proj = torch.nn.Linear(config.clip_vision_model.num_labels, config.transformer.hidden_size, bias=False)
        self.data_proj = torch.nn.Linear(config.transformer.hidden_size, config.transformer.hidden_size)
        self.transformer = PerceptionEncoderVideoTransformer(config.transformer)

    def forward(
        self,
        pixel_values_videos: torch.Tensor,
        padding_mask_videos: Optional[torch.Tensor] = None,
    ) -> BaseModelOutputWithPooling:
        B, N, C, H, W = pixel_values_videos.shape
        backbone_output = self.clip_vision_model(pixel_values_videos.view(B * N, C, H, W)).logits.view(B, N, -1)
        projected = self.proj(F.normalize(backbone_output, dim=-1))
        return self.transformer(self.data_proj(projected), padding_mask=padding_mask_videos)


@dataclass
@auto_docstring
class PerceptionEncoderVideoTextOutput(ModelOutput):
    loss: Optional[torch.FloatTensor] = None
    text_embeds: Optional[torch.FloatTensor] = None
    video_embeds: Optional[torch.FloatTensor] = None
    text_model_output: BaseModelOutputWithPooling = None
    video_model_output: BaseModelOutputWithPooling = None

    def to_tuple(self) -> tuple[Any]:
        return tuple(self[k] if not k.endswith("model_output") else getattr(self, k).to_tuple() for k in self.keys())


@auto_docstring
class PerceptionEncoderVideoWithTextModel(PerceptionEncoderVideoPretrainedModel):
    def __init__(self, config: PerceptionEncoderVideoConfig):
        super().__init__(config)
        self.text_model = AutoModel.from_config(config.text_model)
        self.text_head = PerceptionEncoderVideoContrastiveHead(config.text_model.hidden_size, config.output_dim)
        self.logit_scale = torch.nn.Parameter(torch.tensor([10.0]).log())
        self.logit_bias = torch.nn.Parameter(torch.tensor([-10.0]))

    def _get_text_output(self, input_ids, attention_mask):
        nth_layer = self.config.nth_text_layer
        output = self.text_model(
            input_ids=input_ids, attention_mask=attention_mask, output_hidden_states=nth_layer is not None
        )
        if nth_layer is None:
            text_model_output = output.last_hidden_state
        else:
            text_model_output = output.hidden_states[nth_layer]

        return BaseModelOutputWithPooling(last_hidden_state=text_model_output, pooler_output=text_model_output[:, 0])

    def forward(
        self,
        input_ids: torch.Tensor,
        pixel_values_videos: torch.Tensor,
        attention_mask: Optional[torch.Tensor] = None,
        padding_mask_videos: Optional[torch.Tensor] = None,
        return_loss=False,
    ) -> PerceptionEncoderVideoTextOutput:
        video_model_output = super().forward(pixel_values_videos, padding_mask_videos)
        text_model_output = self._get_text_output(input_ids, attention_mask)

        text_embeds = self.video_text_head(text_model_output.pooler_output)
        video_embeds = self.video_head(video_model_output.pooler_output)

        loss = None
        if return_loss:
            logits_per_text = text_embeds @ video_embeds.t()
            logits_per_text = logits_per_text * self.logit_scale.exp()
            labels = torch.eye(text_embeds.size(0), device=text_embeds.device)
            loss = -F.logsigmoid(labels * logits_per_text).sum() / text_embeds.size(0)

        return PerceptionEncoderVideoTextOutput(
            loss=loss,
            text_embeds=text_embeds,
            video_embeds=video_embeds,
            text_model_output=text_model_output,
            video_model_output=video_model_output,
        )


__all__ = ["PerceptionEncoderVideoModel", "PerceptionEncoderVideoWithTextModel"]
