                hidden_act="gelu",
                hidden_dropout_prob=0.0,
                initializer_range=0.02,
                layer_norm_eps=1e-06,
                layerscale_value=1.0,
                mlp_ratio=4,
                num_attention_heads=6,
                num_channels=3,
                num_hidden_layers=12,
                qkv_bias=True,
                use_swiglu_ffn=False,
